targets.expr:
sum by (workflow_name) (
    max by(workflow_name, rivet_project, rivet_datacenter) (
        rivet_gasoline_workflow_active{rivet_project=~"$project",rivet_datacenter=~"$datacenter",workflow_name=~"$workflow_name"}
        * on(k8s_pod_name) group_left(rivet_project, rivet_datacenter)
        (
            (rivet_gasoline_worker_last_metrics_publish{rivet_project=~"$project",rivet_datacenter=~"$datacenter"} == bool
            on(rivet_project, rivet_datacenter) group_left()
            max by (rivet_project, rivet_datacenter) (rivet_gasoline_worker_last_metrics_publish{rivet_project=~"$project",rivet_datacenter=~"$datacenter"}))
        )
    )
)

