      "src/types.ts": "export type OrderStatus = \"pending\" | \"packed\" | \"shipped\" | \"delivered\";\n\nexport type OrderInput = {\n\tcustomer: string;\n};\n\nexport type Order = {\n\tid: string;\n\tcustomer: string;\n\tstate: OrderStatus;\n\tcreatedAt: number;\n\tupdatedAt: number;\n};\n\nexport const orderFlow: readonly OrderStatus[] = [\n\t\"pending\",\n\t\"packed\",\n\t\"shipped\",\n\t\"delivered\",\n];\n",
      "src/registry.ts": "import { actor, setup, UserError } from \"rivetkit\";\nimport { assertUnreachable } from \"rivetkit/utils\";\nimport type { Order, OrderInput } from \"./types\";\n\nconst orderWorkflow = actor({\n\tcreateState: (c, input): Order => ({\n\t\tid: c.actorId,\n\t\tcustomer: (input as OrderInput).customer,\n\t\tstate: \"pending\",\n\t\tcreatedAt: Date.now(),\n\t\tupdatedAt: Date.now(),\n\t}),\n\tactions: {\n\t\tadvance: (c) => {\n\t\t\tswitch (c.state.state) {\n\t\t\t\tcase \"pending\":\n\t\t\t\t\t// Execute durable operation here\n\t\t\t\t\tc.state.state = \"packed\";\n\t\t\t\t\tbreak;\n\t\t\t\tcase \"packed\":\n\t\t\t\t\t// Execute durable operation here\n\t\t\t\t\tc.state.state = \"shipped\";\n\t\t\t\t\tbreak;\n\t\t\t\tcase \"shipped\":\n\t\t\t\t\t// Execute durable operation here\n\t\t\t\t\tc.state.state = \"delivered\";\n\t\t\t\t\tbreak;\n\t\t\t\tcase \"delivered\":\n\t\t\t\t\t// Execute durable operation here\n\t\t\t\t\tthrow new UserError(\"order already delivered\", {\n\t\t\t\t\t\tcode: \"order_complete\",\n\t\t\t\t\t});\n\t\t\t\tdefault:\n\t\t\t\t\tassertUnreachable(c.state.state);\n\t\t\t}\n\n\t\t\tc.state.updatedAt = Date.now();\n\n\t\t\tc.broadcast(\"orderAdvanced\", c.state);\n\n\t\t\treturn c.state;\n\t\t},\n\n\t\tgetOrder: (c) => c.state,\n\t},\n});\n\nexport const registry = setup({\n\tuse: { orderWorkflow },\n});\n\nexport type Registry = typeof registry;\n",
